var plan;
var loaded;

window.onload = function() {
	if(!window.File && window.FileReader) {
		console.log("This browser does not support the File API");
		return;
	}

	loaded = 0;

	initEventListeners();

	intv = setInterval(monitorPlan, 500);
};

function initEventListeners() {
	$('#loadBtn').change(function() {
		var files = event.target.files;

		var reader = new FileReader();
		reader.addEventListener('loadend', onFileLoaded);
		reader.readAsText(files[0]);
	});
}

function onFileLoaded(event) {
	if(event.target.readyState == FileReader.DONE) {
		plan = JSON.parse(event.target.result);

		do {
			var ltL = Date.parse(plan[0].launchDate);
			var time = new Date().getTime();
			if(ltL <= time) { //time already passed
				plan.splice(0,1);
				console.log('splice');
			}

			if(plan.length == 0) {
				break;
			} else {
				ltL = plan[0].launchDate;
			}
		} while(ltL <= new Date().getTime());

		if(plan.length > 0) {
			loaded = 1;
			$('#status').text('Monitoring Plan');
		}
	}
}

function monitorPlan() {
	$('#utcTime').text(moment.utc().format('MMM Do YY, h:mm:ss a'));
	$('#localTime').text(moment().format('MMM Do YY, h:mm:ss a'));

	if(loaded) {
		if(plan.length == 0) {
			return;
		}
		var p = plan[0];

		var ltL = Date.parse(p.launchDate); //local time
		var ltU = getUTCLaunchTimeStamp(p.launchDate);
		var currentTime = new Date().getTime();
		if(ltL >= (currentTime + 5*60*1000)) {
			// 5 minutes before attack
				for(var i = 0; i < 5; i++) {
					beep();
				}
		}

		if(ltL <= currentTime) {
			plan.splice(0, 1);
			beep();
		}

		if(plan.length == 0) {
			$('#origin').text('');
			$('#target').text('');
			$('#unit').text('');
			$('#ltL').text('');
			$('#ltU').text('');
			clearInterval(intv);
			$('#status').text('Plan Completed');
			return;
		}

		$('#origin').text(p.originName + ' (' + p.origin.x + '|' + p.origin.y + ')');
		$('#target').text(p.targetName + ' (' + p.target.x + '|' + p.target.y + ')');
		$('#unit').text(p.attackUnitType);
		$('#ltL').text(moment(ltL, 'x').format('MMM Do, h:mm:ss a'));
		$('#ltU').text(moment(ltU, 'x').format('MMM Do, h:mm:ss a'));
	}
}

function getUTCLaunchTimeStamp(ld) {
	var ltU = new Date(ld).getTime() + new Date().getTimezoneOffset() * 60000;
	return ltU;
}

function beep() {
	var snd = new  Audio("data:audio/wav;base64,");
	snd.play();
}